mapper.update:
        update community.answer
        set
        answer_time = #{answer_time}
        answer_type=true,
        answer_content=#{answer_content}
where user_name = #{user_name} and form_id=#{form_id};

mapper.select:
SELECT id,answer_type,form_name,deadline,form_creator FROM answer,form
WHERE user_name =#{user_name} and answer.form_id=form.form_id
limit #{begin_num},#{page_size}

SELECT id,user_name,answer_type,form_name,deadline,form_creator FROM answer,form
WHERE answer.form_id=form.form_id and form.form_id=#{form_id}
limit #{begin_num},#{page_size}
